mapper.update:
update md_question 
set md_question_answer = "답변완료"
where md_question_id = #{md_question_id}

update md_question
set question_enable = "Y"
where md_question_id=#{no}

update order_main
set order_status = #{order_status}
where order_id = #{order_id}

update md_question
set question_enable = "N"
where md_question_id=#{no}

mapper.select:
	select md_answer_id, md_answer_member, md_answer_content, md_answer_date, md_question_id
    from md_question_reply
	where md_question_id = #{md_question_id}

SELECT count(om.order_id) cnt FROM order_main om, order_detail od, md md, delivery da, member m , common_code coco where om.member_id = #{member_id}
and md.md_name like concat('%', #{searchWord}, '%')
and om.member_id = m.member_id and om.order_id = od.order_id and od.md_id = md.md_id and om.delivery_id = da.delivery_id and coco.code_class = 'order_status' and om.order_status = coco.code_id

mapper.update:
update md
set md_name = #{md_name}
	, md_oneline = #{md_oneline}
	, md_price = #{md_price}
	, md_category = #{md_category}
	, md_abv = #{md_abv}


		,md_thumbnail_name = #{md_thumbnail_name}
