		,md_detail_name = #{md_detail_name}
		, md_detail_path = #{md_detail_path}

	, md_content = #{md_content}
	, md_standard_stock = #{md_standard_stock}
	, md_sales_rate = #{md_sales_rate}
	, md_stock = #{md_stock}
	, md_sweet = #{md_sweet}
	, md_sour = #{md_sour}
	, md_sparkling = #{md_sparkling}
	, md_use = #{md_use}
